import React, { useEffect } from 'react'
import styled from 'styled-components'
import 'react-alice-carousel/lib/alice-carousel.css'
import Kkwe from '../images/Kkwe.png'
import IoT from '../images/iot.gif'
import Dash from '../images/dash.gif'
import Crud from '../images/Crud.png'

import Bright from '../images/Bright.png'
import Rot from '../images/Rot.png'
import Nike from '../images/Nike.png'
import AOS from 'aos';
import 'aos/dist/aos.css';

const Container = styled.div`
height: 100%;
margin:0 auto;
overflow:hidden;
width: 80vw;
margin-bottom:5vw;
@media (min-width: 1px) and (max-width: 426px) {
  margin-bottom:20vw;
  width: 80vw;
  }
`
const ImgHolder = styled.div`
width:60vw;

transition: all 0.2s ease-in-out;
@media (min-width: 1px) and (max-width: 426px) {
  width:100%;

 }
`

const Holder = styled.div`
display:flex;
margin: 0px auto;
padding-bottom: 20px;
flex-direction:row;
width:75vw;
height: inherit;
margin: 5vw 0vw;
&:hover{
  width:75vw;
 transition: all 2s ease-in-out;


}
@media (min-width: 1px) and (max-width: 426px) {
 width:100%;
 display: flex;
 flex-direction: column;
 margin: 0vw 0vw;
 }
`

const HolderInner = styled.div`
display:flex;
padding-bottom: 0px;
flex-direction:column;
width:100%;
@media (min-width: 1px) and (max-width: 426px) {
  margin: 8vw 0vw 0.5vw 0vw;
  flex-direction:column;
 }
`


const ProjectHeading = styled.h1`
font-size:6vw;
color:white;
font-family:arial;
font-weight:bolder;
width:85%;
cursor: pointer;
@media (min-width: 1px) and (max-width: 426px) {
  font-size:9vw;
  margin-bottom:2vw;
 }
`
const Sup = styled.sup`

color:#8BC34A;
font-family:arial;
font-weight:bolder;
cursor: pointer;
@media (min-width: 1px) and (max-width: 426px) {


 }
`

const Heading = styled.h1`
font-size:1vw;
color:white;
font-family:arial;
margin: 0.5vw 0vw 0.5vw 0.5vw;
@media (min-width: 1px) and (max-width: 426px) {
  font-size: 3.5vw;
 }
`
const Img = styled.img`
width:100%;
height:100%
@media (min-width: 1px) and (max-width: 426px) {
  margin-top:5vw; 
  width:100%;
  height:100%
 }
`

const StackMenu = styled.div`
width:100%;
height:100%;
color:red;

`
const StackMenuHeading = styled.p`
font-size:0.8vw;
color:white;
font-family:arial;
margin: 0.5vw 0vw 0.5vw 0.5vw;
color:red;

`



function Gallery() {

  useEffect(() => {
    AOS.init({
      duration: 1000
    })
  }, [])

  const [projectsData,setProjectsData] = useState([])

  let projects = [
    {
      url: "https://odinsgate.co.za/",
      sup: "IOT",
      ProjectHeading: "SMART POT",
      heading: "IOT BASED PLANT MONITOR",
      frontend: "REACT.JS - CHART.JS - STYLED COMPONENTS ",
      backend: "NODE.JS - EXPRESS.JS",
      github: IoT,
      image: IoT,
      showStack: false,
    }
  ]

  const toggleStackMenu = (project) => {
    if (project.showStack == false) {
        project.showStack = true
    }
  }
  return (

    <Container >
      {projects?.map((project, index) => {
        return (
          <Holder data-aos='slide-up'>

            <HolderInner>
              <a href={project.url} target="_blank" rel="noopener noreferrer">
                <span><Sup>{project.sup}</Sup><ProjectHeading  >{project.ProjectHeading}</ProjectHeading> </span>
              </a>
              <Heading>{project.heading}</Heading>
              <div>
                <StackMenuHeading onClick={() => { toggleStackMenu(project) }}>View Stack</StackMenuHeading>

                {project?.showStack &&
                  <StackMenu>
                    <Sup>Front-End Stack</Sup>
                    <Heading  >{project.frontend}</Heading>
                    <Sup>Back-End</Sup>
                    <Heading  >{project.backend}</Heading>
                  </StackMenu>
                }
              </div>
              <Heading  >

                <a href={project.github} target="_blank" rel="noopener noreferrer">
                  VIEW CODE
                </a>
              </Heading>
            </HolderInner>

            <ImgHolder>
              <a href={project.url} target="_blank" rel="noopener noreferrer">
                <Img src={project.image} alt="" />
              </a>
            </ImgHolder>


          </Holder>
        )
      })}




    </Container>
  );
}

export default Gallery
