import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import 'react-alice-carousel/lib/alice-carousel.css'
import Kkwe from '../images/Kkwe.png'
import IoT from '../images/iot.gif'
import Dash from '../images/dash.gif'
import Crud from '../images/Crud.png'

import Bright from '../images/Bright.png'
import Rot from '../images/Rot.png'
import Nike from '../images/Nike.png'
import AOS from 'aos';
import 'aos/dist/aos.css';

const Container = styled.div`
height: 100%;
margin:0 auto;
overflow:hidden;
width: 80vw;
margin-bottom:5vw;
@media (min-width: 1px) and (max-width: 426px) {
  margin-bottom:20vw;
  width: 80vw;
  }
`
const ImgHolder = styled.div`
width:60vw;

transition: all 0.2s ease-in-out;
@media (min-width: 1px) and (max-width: 426px) {
  width:100%;

 }
`

const Holder = styled.div`
display:flex;
margin: 0px auto;
padding-bottom: 20px;
flex-direction:row;
width:75vw;
height: inherit;
margin: 5vw 0vw;

@media (min-width: 1px) and (max-width: 426px) {
 width:100%;
 display: flex;
 flex-direction: column;
 margin: 0vw 0vw;
 }
`

const HolderInner = styled.div`
display:flex;
padding-bottom: 0px;
flex-direction:column;
width:100%;
@media (min-width: 1px) and (max-width: 426px) {
  margin: 8vw 0vw 0.5vw 0vw;
  flex-direction:column;
 }
`


const ProjectHeading = styled.h1`
font-size:6vw;
color:white;
font-family:arial;
font-weight:bolder;
width:85%;
cursor: pointer;
@media (min-width: 1px) and (max-width: 426px) {
  font-size:9vw;
  margin-bottom:2vw;
 }
`
const Sup = styled.sup`

color:#8bc34a;
font-family:arial;
font-weight:bolder;
cursor: pointer;
@media (min-width: 1px) and (max-width: 426px) {


 }
`

const Heading = styled.h1`
font-size:1vw;
color:white;
font-family:arial;

@media (min-width: 1px) and (max-width: 426px) {
  font-size: 3.5vw;
 }
`
const HeadingF = styled.h1`
font-size:0.7vw;
color:white;
font-family:arial;
margin: 8px 0px;
@media (min-width: 1px) and (max-width: 426px) {
  font-size: 3.5vw;
 }
`
const HeadingViewCode = styled.h1`
font-size:0.7vw;
color:white;
font-family:arial;
margin: 1vw 0vw 1vw 0vw;
@media (min-width: 1px) and (max-width: 426px) {
  font-size: 3.5vw;
 }
`

const Img = styled.img`
width:100%;
height:100%
@media (min-width: 1px) and (max-width: 426px) {
  margin-top:5vw; 
  width:100%;
  height:100%
 }
`

const StackMenu = styled.div`
width:100%;
height:100%;
color:red;
line-height:22px;
margin: 0.5vw 0vw 0.5vw 0.5vw;
`
const TabHolder = styled.div`
display:flex;
`
const Tab = styled.button`
color:white;
padding:10px 20px;
border:none;
background:none;
cursor:pointer;
&:hover {
  background-color: lightblue;
}
`
const StackMenuHeading = styled.p`
font-size:0.8vw;
color:white;
font-family:arial;
margin: 0.8vw 0vw ;
color:red;

@media (min-width: 1px) and (max-width: 426px) {
  font-size: 3.5vw;

 }
`



function Gallery() {
const [first, setfirst] = useState()
const [filter, setFilter] = useState(null)

  useEffect(() => {
    AOS.init({
      duration: 1000
    })
  }, [])

  let projects = [
    {
      url: "https://odinsgate.co.za/",
      sup: "IOT",
      ProjectHeading: "SMART PLANT",
      heading: "IOT BASED PLANT MONITOR",
      frontend: "REACT.JS - CHART.JS - STYLED COMPONENTS - AXIOS ",
      backend: "MYSQL - NODE.JS - EXPRESS.JS",
      github: "https://github.com/Zeus540/IOTDashboard",
      image: IoT,
      showStack: false,
      catergory:"IOT"
    },
    {
      url: "https://dash.odinsgate.co.za/",
      sup: "CRM",
      ProjectHeading: "NEXT",
      heading: "WEB BASED CRM SYSTEM",
      frontend: "REACT.JS - CHART.JS - STYLED COMPONENTS - AXIOS ",
      backend: "MYSQL - NODE.JS - EXPRESS.JS",
      github: "https://github.com/Zeus540/E-Commerce-Store-NYX",
      image: Dash,
      showStack: false,
      catergory:"CRM"
    }
  ]

  const [projectsData, setProjectsData] = useState(projects)



  const toggleStackMenu = (project) => {
    if (project.showStack == false) {
      project.showStack = true
      setProjectsData([...projectsData])
    } else {
      project.showStack = false
      setProjectsData([...projectsData])
    }
  }

  const handleTabChange = (e)=>{
    console.log(e.target.value)
    setFilter(e.target.value)
  }
  return (

    <Container >

      <TabHolder>
      <Tab value="IOT" onClick={(e)=>{handleTabChange(e)}}>IOT</Tab>
      <Tab value="CRM" onClick={(e)=>{handleTabChange(e)}}>CRM</Tab>
      <Tab value="3D" onClick={(e)=>{handleTabChange(e)}}>3D MODELS</Tab>

      </TabHolder>
      {projectsData?.filter((p)=>{
          if(filter == null){
            return projectsData
          }else{
            return (
              p.catergory === filter
            )

          }
      }).map((project, index) => {

        return (
          <Holder data-aos='slide-up'>

            <HolderInner>
              <a href={project.url} target="_blank" rel="noopener noreferrer">
                <span><Sup>{project.sup}</Sup><ProjectHeading  >{project.ProjectHeading}</ProjectHeading> </span>
              </a>
              <Heading>{project.heading}</Heading>
              <div>
                <StackMenuHeading onClick={() => { toggleStackMenu(project) }}>View Stack</StackMenuHeading>

                {project?.showStack &&
                  <StackMenu>
                    <Sup>Front-End</Sup>
                    <HeadingF>{project.frontend}</HeadingF>
                    <Sup>Back-End</Sup>
                    <HeadingF  >{project.backend}</HeadingF>
                    <HeadingViewCode  >
                      <a href={project.github} target="_blank" rel="noopener noreferrer">
                        VIEW CODE
                      </a>
                    </HeadingViewCode>
                  </StackMenu>
                }
              </div>



            </HolderInner>

            <ImgHolder>
              <a href={project.url} target="_blank" rel="noopener noreferrer">
                <Img src={project.image} alt="" />
              </a>
            </ImgHolder>


          </Holder>
        )
      })}




    </Container>
  );
}

export default Gallery
